import styled from 'styled-components'

export const HeaderWarpper = styled.div`
height:31px;
width:100%;
background-color: #e3e4e5;
color:#999;
`
export const Headercenter = styled.div`
margin:0 auto;
width:1190px;
font-size:10px;
height:14px;
line-height:28px;
`
export const Headercity =styled.div`
float:left;
cursor:pointer;
`

export const Headerright =styled.div`
float:right;
cursor:pointer;
display:flex;
flex-wrap:nowrap;
flex-direction:row;

`
export const Headerreg = styled.a`
color:red;
margin:0 5px;
`
export const Header_a= styled.a`
box-sizing:border-box;
width:70px;
border-left:1px solid #ccc;
margin-left:5px;
padding:0 10px;
p{
  color:red;
}
:hover{
  color:red
}
`
export const Header_login= styled.a`
:hover{
  color:red
}
`
