import React,{Component} from 'react'
import {Pubu_warpper} from './style'
import axios from 'axios'



class Pubu extends Component {
  constructor(props){
    super(props)
    this.state={
      data:[],
      ultop:952,
      str:[]
    }
  }
  getpubu(){
      let lastget=0;
      var str =[];
      for(let i=lastget;i<2;i++){
        str.push(
          <li className="more2_item" >
          <div className="img_text">
            <img className="img_pubu" src={this.state.data[i].src}/>
            <p className="txt_name">{this.state.data[i].text}</p>
          </div>
        </li>)
        lastget++;
      }
      return str;
  }
 
componentDidMount(){
  axios.get('/api/pubu.json').then((res)=>{
    this.setState({
      data:res.data.data
    })
  })         
}

componentDidUpdate(){
