import {Pubu_warpper} from './style'
import axios from 'axios'



class Pubu extends Component {
  constructor(props){
    super(props)
    this.state={
      data:[],
      ultop:952,
      str:[]
    }
  }
  
  getpubu(list){
    let lastget=0;
    var str =[];
    for(let i=lastget;i<10;i++){
      str.push(
        <li className="more2_item" >
        <div className="img_text">
          <img className="img_pubu" src={list[i].src}/>
          <p className="txt_name">{list[i].text}</p>
        </div>
      </li>)
      lastget++;
    }
    }
 
componentDidMount(){
  axios.get('/api/pubu.json').then((res)=>{
    this.setState({
      data:res.data.data,
    })
  })
}
componentDidUpdate(){
  console.log(this.props.scroll + this.props.clheight)
}

  render(){
   
    return(
