import styled from 'styled-components'

export const J_ng = styled.div`
    width: 1190px;
    margin-left: auto;
    margin-right: auto;
    height: 1000px;
    position: relative;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    padding-left: 190px;
    margin-bottom: 20px;
    margin-top:20px;
    .nice_good{
      position: absolute;
    left: 0;
    top: 0;
    width: 190px;
    height: 100%;
    background-image: url("https://misc.360buyimg.com/mtd/pc/index_2019/1.0.0/assets/img/e222ff5affd7051c21a5df1af734893b.png");
    } no-repeat
    .recommends{
      position:relative;
      height:100%;
      margin-left:10px;
      background:white;
      overflow:hidden;
      .good-list{
        padding-left:20px;
        padding-right:20px;
        height:100%;
        width:100%;
        box-sizing:border-box;
        white-space:nowrap;
        img{
          padding-top:40px;
        }
      }
    }
　　.swiper-wrapper{
    transition-timing-function:linear;
    }
    .banner3{
      height:100%;
      text-overflow:ellipsis;
    }
`
