import styled from "styled-components";

export const Inner_warpper = styled.div`
  position: relative;

`
export const Inner_grid = styled.div`
width:1190px;
margin:0 auto;
height:480px;
position: relative;
`
export const Inner_left = styled.div`
  
    position: relative;
    float: left;
    width:190px;
    height: 480px;
    ul{
      overflow:hidden;
      padding:10px 0;
      height:450px;
      background:#fefefe;
      color:#636363;
      margin-top:10px;
    }
    li{
      padding-left: 18px;
      height: 25px;
      line-height: 25px;
      font-size:15px;
    }
    li:hover{
      background:#d9d9d9
    }
  .J_FOCUS2{
    z-index:2;
    display:none;
    overflow:hidden;
    position:absolute;
    top:10px;
    left:190px;
    width:790px;
    height:470px;
    border:1px solid #ddd;
    background:#fefefe;
}
.none{
  display:none;
}
.active{
  display:block;
}
.cate_part{
  margin-top:20px;
  font-size:12px;
dl{
  position:relative;
  padding-left: 80px;
}
dt{
  color: #333;
  color:black;
  overflow: hidden;
    position: absolute;
    left: 0;
    top: 6px;
    width: 70px;
    text-align: right;
    font-weight: 700;
    white-space: nowrap;
    text-overflow: ellipsis;
}
dd{
  color:#666;
  overflow: hidden;
  padding: 5px 0;
}
a{
  margin-left:10px;
  float: left;
    margin: 3px 0;
    padding: 0 5px;
    height: 14px;
    line-height: 10px;
    white-space: nowrap;
}
    }
    
`
export const Inner_Swiper = styled.div`
float:left;
width:590px;
height:470px;
padding-left:10px;
padding-top:10px;
.swiper-container{
  --swiper-navigation-size:12px;
  --swiper-theme-color: rgba(255,255,255,1);
  
}
.swiper-button-prev{
  margin-left:-10px;
  border-top-right-radius: 18px;
  border-bottom-right-radius: 18px;
}
.swiper-button-next{
  margin-right:-10px;
  border-top-left-radius: 18px;
  border-bottom-left-radius: 18px;
}
.button{
  width: 25px;
  height: 35px;
  color:#fff;
  background-color: rgba(0,0,0,.15);
}
.swiper-pagination-bullet{
  border: 1px solid rgba(0,0,0,.1);
}
.swiper-pagination{
  text-align:left;
  left:20px;
  bottom:15px;
}
`
