import React,{Component} from 'react'
import {Pubu_warpper} from './style'
import axios from 'axios'
let tt=0;
let lastget=0;
let str =[];


class Pubu extends Component {
  constructor(props){
    super(props)
    this.state={
      ultop:952,
      nb:0,
      str:[],
      page:0
    }
  }
  componentWillReceiveProps (nextProps){
    let list =this.props.data; 
      for(let i=tt;i<(tt+1)*10;i++){
        if(list[i]!=undefined&&lastget<lastget+10){
          str.push(list[i])
          lastget++
        // str.push(
        //   <li className="more2_item" key={list[i].text+i}>
        //   <div className="img_text">
        //   <img className="img_pubu" src={list[i].src}/>
        //     <p className="txt_name">{list[i].text}</p>          
        //     </div>
        // </li>)
        console.log(lastget)
        }
      }
    
      this.setState({
        str,
        nb:Math.floor(lastget/10)-1,
        page:list.length/10
      })
    
  }

  render(){
    let top =Math.floor((this.props.scroll + this.props.clheight -this.state.ultop)/300);
    let list = this.state.str;
       return(
      <Pubu_warpper ref="ul" >
        <div className="floor">
          <h3 className="floor_hit">
            为你推荐
          </h3>
        </div>
      <div className="grid_c1">
        <ul className="more2_list" >
         {top>=tt?
          list.map((item,index)=>{
           return(
            <li className="more2_item" key={item.text}>
            <div className="img_text">
              <img className="img_pubu" src={item.src}/>
              <p className="txt_name">{item.text}</p>
            </div>
          </li>
           )
         })&&tt++:""
         }
        </ul>
      </div>
      </Pubu_warpper>
    )
  }
}

export default Pubu;
