import React,{Component} from 'react'
import {Pubu_warpper} from './style'
import axios from 'axios'

class Pubu extends Component {
  constructor(props){
    super(props)
    this.state={
      data:''
    }
  }
componentDidMount(){
  axios.get('/api/pubu.json').then((res)=>{
    this.setState({
      data:res.data
    })
  })

}
  render(){
    return(
      <Pubu_warpper>
        <div className="floor">
          <h3 className="floor_hit">
            为你推荐
          </h3>
        </div>
      <div className="grid_c1">
        <ul className="more2_list">
         {this.state.data.map((item,index)=>{
           return(
            <li className="more2_item">
            <div className="img_text">
              <img className="img_pubu" src={item.src}/>
           <p className="txt_name">{item.text}</p>
            </div>
          </li>
           )
         })
         }
        </ul>
      </div>
      </Pubu_warpper>
    )
  }
}

export default Pubu;
