import styled from 'styled-components'

export const Search_warpper =styled.div`
position:relative;
height:140px;
width:1140px;
margin:0 auto;
overflow:hidden;
`
export const Search_img = styled.div`
float:left;
img{
width:190px;
height:120px;
}
`
export const Search_input = styled.div`
position:relative;
width:546px;
height:32px;
input{
z-index:1;
position:absolute;
top:30px;
left:240px;
width:488px;
height:32px;
border:1px solid red;
background: transparent;
}
.sw_input{
position:absolute;
top:30px;
left:240px;
width:488px;
height:36px;
font-size:14px;
color:#ccc;
line-height:36px;
padding-left:20px;
}
.iconf{
  position: absolute;
  background-color: #e1251b;
  color:#fff;
  border:none;
  width:58px;
  height:32px;
  font-size:20px;
  right:-40px;
}
`
