import React,{Component} from 'react'
import {Pubu_warpper} from './style'
import axios from 'axios'



class Pubu extends Component {
  constructor(props){
    super(props)
    this.state={
      ultop:952,
      str:[],
      nb:0
    }
  }
  getpubu(){
      let list =this.props.data; 
      let lastget=0;
      var str =[];
      let text= 0;
      for(let i=lastget;i<lastget+2;i++){
        if(list[i]!=undefined){
        str.push(
          <li className="more2_item" >
          <div className="img_text">
          <img className="img_pubu" src={list[i].src}/>
            <p className="txt_name">{list[i].text}</p>          
            </div>
        </li>)
        lastget=lastget+2;
        text++;
        this.setState({
          nb:text
        })
        }
      }
      return str;
  }
 


  render(){
    let top =Math.floor((this.props.scroll + this.props.clheight -this.state.ultop)/300);
    console.log(top)
    return(
      <Pubu_warpper ref="ul" >
        <div className="floor">
          <h3 className="floor_hit">
            为你推荐
          </h3>
        </div>
      <div className="grid_c1">
        <ul className="more2_list" >
         {/* {
          list.map((item,index)=>{
           return(
            <li className="more2_item" key={item.text}>
            <div className="img_text">
              <img className="img_pubu" src={item.src}/>
              <p className="txt_name">{item.text}</p>
            </div>
          </li>
           )
         })
         } */}
         {top>0?
          this.getpubu():""
         }
        </ul>
      </div>
      </Pubu_warpper>
    )
  }
}

export default Pubu;
