    super(props)
    this.state={
      ultop:952,
      nb:0,
      str:[],
      page:0
    }
  }
  componentDidMount(){
    document.addEventListener('scroll',this.ajaxPull)
}
componentWillUnmount(){
    document.removeEventListener('scroll',this.ajaxPull);
}
